import React from "react";

import { makeStyles } from "@material-ui/core/styles";

import Divider from "@material-ui/core/Divider";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import GridList from "@material-ui/core/GridList";
import { GridListTile } from "@material-ui/core";

const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
  gridRoot: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "space-around",
    overflow: "hidden",
    // overflow: "auto",
    // backgroundColor: theme.palette.background.paper,
  },
  gridList: {
    // flexWrap: "nowrap",
    // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
    transform: "translateZ(0)",
  },
  gridTitle: {
    // color: theme.palette.primary.light,
  },
});

export default function Block(props) {
  const classes = useStyles();
  const blocks = props.getBlocksHandler();
  console.log(blocks);
  return (
    <div className={classes.gridRoot}>
      <GridList className={classes.gridList} cols={2.5}>
        {blocks.map((block, index) => (
          <GridListTile key={index} style={{ height: "300px", width: "550px" }}>
            <Card
              className={classes.root}
              variant="outlined"
              style={{ paddingLeft: "25px" }}
            >
              <CardContent>
                <Typography variant="h5" component="h2">
                  {index === 0 ? "Genesis Block" : "Block " + index}
                </Typography>
                <Divider />
                <Typography>Hash of this block</Typography>
                <Typography
                  className={classes.title}
                  color="textSecondary"
                  gutterBottom
                >
                  {block.hash}
                </Typography>
                <Typography>Hash of previous block</Typography>
                <Typography
                  className={classes.title}
                  color="textSecondary"
                  gutterBottom
                >
                  {block.previousHash}
                </Typography>
                <Divider />
                <Typography className={classes.title}>Nonce</Typography>
                <Typography className={classes.pos} color="textSecondary">
                  {block.nonce}
                </Typography>
                <Divider />
                <Typography className={classes.title}>Time stamp</Typography>
                <Typography className={classes.pos} color="textSecondary">
                  {block.timestamp}
                </Typography>
              </CardContent>
              <CardActions>
                <Button size="small">Learn More</Button>
              </CardActions>
            </Card>
          </GridListTile>
        ))}
      </GridList>
    </div>
  );
}
