import React from "react";
import BlockGrid from "./BlockGrid";
import BlockContentTable from "./BlockContentTable";
import Divider from "@material-ui/core/Divider";
const { Blockchain } = require("savjeecoin");

class BlockchainService extends React.Component {
  blockchainInstance = new Blockchain();

  constructor(props) {
    super(props);
    this.blockchainInstance.difficulty = 1;
    this.blockchainInstance.minePendingTransactions("my-wallet-address");

    this.getBlocks = this.getBlocks.bind(this);
  }

  state = {
    selectedBlockId: null,
  };

  handleClick = (blockId) => {
    this.setState({ selectedBlockId: blockId });
  };

  getBlocks() {
    return this.blockchainInstance.chain;
  }

  render() {
    return (
      <div>
        <BlockGrid getBlocksHandler={this.getBlocks} />
        <Divider />
        <h1>Transaction inside Block temp</h1>
        <BlockContentTable blocks={this.getBlocks} onChange={handleClick} />
      </div>
    );
  }
}

export default BlockchainService;
