import React, { useState } from "react";
import { Route, Switch } from "react-router-dom";

import Home from "./Home";
import PendingTransactions from "./PendingTransactions";
import Buttons from "./Buttons";
import Wallet from "./Wallet";

import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import CssBaseline from "@material-ui/core/CssBaseline";
import Drawer from "@material-ui/core/Drawer";
import Box from "@material-ui/core/Box";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import List from "@material-ui/core/List";
import Typography from "@material-ui/core/Typography";
import Divider from "@material-ui/core/Divider";
import IconButton from "@material-ui/core/IconButton";
import Container from "@material-ui/core/Container";
import Grid from "@material-ui/core/Grid";
import Link from "@material-ui/core/Link";
import MenuIcon from "@material-ui/icons/Menu";
import ChevronLeftIcon from "@material-ui/icons/ChevronLeft";
// import Badge from "@material-ui/core/Badge";
// import NotificationsIcon from "@material-ui/icons/Notifications";

// import { mainListItems, secondaryListItems } from "./listItems";

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {"Copyright © "}
      <Link color="inherit" href="https://material-ui.com/">
        DrorTsky
      </Link>{" "}
      {"."}
    </Typography>
  );
}

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  toolbar: {
    paddingRight: 24, // keep right padding when drawer closed
  },
  toolbarIcon: {
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-end",
    padding: "0 8px",
    ...theme.mixins.toolbar,
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(["width", "margin"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(["width", "margin"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: 36,
  },
  menuButtonHidden: {
    display: "none",
  },
  title: {
    flexGrow: 1,
  },
  drawerPaper: {
    position: "relative",
    whiteSpace: "nowrap",
    width: drawerWidth,
    transition: theme.transitions.create("width", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  drawerPaperClose: {
    overflowX: "hidden",
    transition: theme.transitions.create("width", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    width: theme.spacing(7),
    [theme.breakpoints.up("sm")]: {
      width: theme.spacing(9),
    },
  },
  appBarSpacer: theme.mixins.toolbar,
  content: {
    flexGrow: 1,
    height: "100vh",
    overflow: "auto",
  },
  container: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
  },
  paper: {
    padding: theme.spacing(2),
    display: "flex",
    overflow: "auto",
    flexDirection: "column",
  },
  fixedHeight: {
    height: 240,
  },
}));

export default function Dashboard() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(true);
  const handleDrawerOpen = () => {
    setOpen(true);
  };
  const handleDrawerClose = () => {
    setOpen(false);
  };

  const [theDiff, setDiff] = useState(1);
  const [theReward, setReward] = useState(100);

  const [transactionFromAddress, setTransactionFromAddress] = useState("");
  const [transactionToAddress, setTransactionToAddress] = useState("");
  const [transactionAmount, setTransactionAmount] = useState("");
  const [isChanged, setIsChanged] = useState(true);
  const [isMined, setIsMined] = useState(true);
  const [minersAddress, setMinersAddress] = useState("");

  const [blocks, setPendingBlocks] = useState([]);

  const [wallet, setWallet] = useState({
    balanceDror: 1000,
    balanceVlad: 1000,
    balanceMiner: 1000,
    transactionsDror: [],
    transactionsVlad: [],
    transactionsMiner: [],
  });
  // const [walletTransactions, setWalletTransactions] = useState([]);
  // const [walletBalance, setWalletBalance] = useState(1000);

  return (
    <div>
      <div className={classes.root}>
        <CssBaseline />
        <AppBar
          position="absolute"
          className={clsx(classes.appBar, open && classes.appBarShift)}
        >
          <Toolbar className={classes.toolbar}>
            <IconButton
              edge="start"
              color="inherit"
              aria-label="open drawer"
              onClick={handleDrawerOpen}
              className={clsx(
                classes.menuButton,
                open && classes.menuButtonHidden
              )}
            >
              <MenuIcon />
            </IconButton>
            <Typography
              component="h1"
              variant="h6"
              color="inherit"
              noWrap
              className={classes.title}
            >
              DNVcoin
            </Typography>
            <Buttons
              difficulty={setDiff}
              reward={setReward}
              setTransactionFromAddress={setTransactionFromAddress}
              setTransactionToAddress={setTransactionToAddress}
              setTransactionAmount={setTransactionAmount}
              setIsChanged={setIsChanged}
              isChanged={isChanged}
              blocks={blocks}
              setIsMined={setIsMined}
              isMined={isMined}
              wallet={wallet}
              minersAddress={minersAddress}
            />
          </Toolbar>
        </AppBar>
        <Drawer
          variant="permanent"
          classes={{
            paper: clsx(classes.drawerPaper, !open && classes.drawerPaperClose),
          }}
          open={open}
        >
          <div className={classes.toolbarIcon}>
            <IconButton onClick={handleDrawerClose}>
              <ChevronLeftIcon />
            </IconButton>
          </div>
          <Divider />
          {/* <List>{mainListItems}</List>
          <Divider />
          <List>{secondaryListItems}</List> */}
        </Drawer>
        <main className={classes.content}>
          <div className={classes.appBarSpacer} />
          <Container maxWidth="lg" className={classes.container}>
            <Grid container spacing={3}>
              {/* <Router> */}
              {/* <Switch> */}
              {/* <Route exact path="/"> */}
              <Home
                difficulty={theDiff}
                reward={theReward}
                transactionFromAddress={transactionFromAddress}
                transactionToAddress={transactionToAddress}
                transactionAmount={transactionAmount}
                isChanged={isChanged}
                pendingBlocks={setPendingBlocks}
                isMined={isMined}
                setWallet={setWallet}
                setMinersAddress={setMinersAddress}
              />
              {/* </Route> */}
              {/* <Route path="/pending_transactions">
                  <PendingTransactions />
                </Route>
                <Route path="/walletOne">
                  <Wallet />
                </Route>
                <Route path="/walletTwo">
                  <Wallet />
                </Route>
                <Route path="/miner">
                  <Wallet />
                </Route> */}
              {/* </Switch> */}
            </Grid>
          </Container>
        </main>
      </div>
      <footer>
        <Box pt={4}>
          <Copyright />
        </Box>
      </footer>
    </div>
  );
}
