import React, { useState } from "react";

import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import ButtonGroup from "@material-ui/core/ButtonGroup";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Input from "@material-ui/core/Input";

const useStyles = makeStyles((theme) => ({
  buttons: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    "& > *": {
      margin: theme.spacing(1),
    },
  },
  inputFields: {
    padding: "20px",
  },
}));

export default function Buttons(props) {
  const classes = useStyles();
  const [
    openFormDialogTransaction,
    setOpenFormDialogTransaction,
  ] = React.useState(false);

  const handleClickOpen = () => {
    setOpenFormDialogTransaction(true);
  };

  const handleClose = () => {
    setOpenFormDialogTransaction(false);
  };

  const [openFormDialogSettings, setOpenFormDialogSettings] = React.useState(
    false
  );
  const handleClickOpenSettings = () => {
    setOpenFormDialogSettings(true);
  };

  const handleCloseSettings = () => {
    setOpenFormDialogSettings(false);
  };

  //THE NEW_TRANSACTION INPUT USE_STATES
  const [transactionState, setTransactionState] = useState({
    fromAddress: "",
    toAddress: "",
    amount: "",
  });

  // THE SETTING INPUT USE_STATES
  const [settingsDifficultyValue, setSettingsDifficultyValue] = useState(1);
  const [settingsRewardValue, setSettingsRewardValue] = useState(100);

  //HANDLES SUBMISSION FROM SETTINGS FORM
  const handleSubmit = (e) => {
    if (settingsDifficultyValue !== null) {
      props.difficulty(settingsDifficultyValue);
    }
    if (settingsRewardValue !== null) {
      props.reward(settingsRewardValue);
    }
    e.preventDefault();
    handleCloseSettings();
  };

  return (
    <div className={classes.buttons}>
      <ButtonGroup variant="text" aria-label="text primary button group">
        <Button
          style={{ fontSize: "18px", fontWeight: "bold" }}
          onClick={handleClickOpen}
          color="inherit"
        >
          ADD TRANSACTION
        </Button>
        <Dialog
          open={openFormDialogTransaction}
          onClose={handleClose}
          aria-labelledby="form-dialog-title"
        >
          <DialogTitle id="form-dialog-title">
            ADD A NEW TRANSACTION
          </DialogTitle>
          <DialogContent>
            <DialogContentText>
              Transfer some money to someone!
            </DialogContentText>
            <TextField
              autoFocus
              margin="dense"
              id="fromAddress"
              name="fromAddress"
              label="From Address"
              type="email"
              fullWidth
            />
            <TextField
              autoFocus
              margin="dense"
              id="toAddress"
              name="toAddress"
              label="To Address"
              type="email"
              fullWidth
            />
            <TextField
              autoFocus
              margin="dense"
              id="amount"
              name="amount"
              label="Amount"
              type="email"
              fullWidth
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose} color="primary">
              Cancel
            </Button>
            <Button onClick={handleClose} color="primary">
              Sign and create transaction
            </Button>
          </DialogActions>
        </Dialog>
        <Button
          onClick={handleClickOpenSettings}
          style={{ fontSize: "18px", fontWeight: "bold" }}
          color="inherit"
        >
          Settings
        </Button>
        <Dialog
          open={openFormDialogSettings}
          onClose={handleCloseSettings}
          aria-labelledby="form-dialog-title"
        >
          <DialogTitle id="form-dialog-title">SETTINGS</DialogTitle>
          <form action="/" method="POST" onSubmit={handleSubmit}>
            <DialogContent>
              <Input
                className={classes.inputFields}
                placeholder="difficulty"
                autoFocus
                type="text"
                margin="dense"
                fullWidth
                inputProps={{ "aria-label": "description" }}
                onChange={(e) => {
                  setSettingsDifficultyValue(e.target.value);
                }}
              />

              <Input
                className={classes.inputFields}
                placeholder="reward"
                autoFocus
                type="text"
                margin="dense"
                fullWidth
                inputProps={{ "aria-label": "description" }}
                onChange={(e) => {
                  setSettingsRewardValue(e.target.value);
                }}
              />
            </DialogContent>
            <DialogActions>
              <Button onClick={handleCloseSettings} color="primary">
                Cancel
              </Button>
              <Button type="submit" label="Submit" color="primary">
                Confirm
              </Button>
            </DialogActions>
          </form>
        </Dialog>
      </ButtonGroup>
    </div>
  );
}
