import React from "react";

import { makeStyles } from "@material-ui/core/styles";
import Divider from "@material-ui/core/Divider";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";

const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});

export default function Block(props) {
  const classes = useStyles();
  const blocks = props.getBlocksHandler();
  console.log(blocks);
  return (
    <div>
      {blocks.map((block, index) => {
        return (
          <GridList className={classes.gridList} cols={1}>
            <GridListTile style={{ height: "340px" }}>
              <Card className={classes.root} variant="outlined">
                <CardContent>
                  <Typography variant="h5" component="h2">
                    {index === 0 ? "Genesis Block" : "Block " + index}
                  </Typography>
                  <Divider />
                  <Typography>Hash of this block</Typography>
                  <Typography
                    className={classes.title}
                    color="textSecondary"
                    gutterBottom
                  >
                    {block.hash}
                  </Typography>
                  <Typography>Hash of previous block</Typography>
                  <Typography
                    className={classes.title}
                    color="textSecondary"
                    gutterBottom
                  >
                    {block.previousHash}
                  </Typography>
                  <Divider />
                  <Typography className={classes.title}>Nonce</Typography>
                  <Typography className={classes.pos} color="textSecondary">
                    {block.nonce}
                  </Typography>
                  <Divider />
                  <Typography className={classes.title}>Time stamp</Typography>
                  <Typography className={classes.pos} color="textSecondary">
                    {block.timestamp}
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button size="small">Learn More</Button>
                </CardActions>
              </Card>
            </GridListTile>
          </GridList>
        );
      })}
    </div>
  );
}
