import React from "react";
import BlockGrid from "./BlockGrid";
import BlockContentTable from "./BlockContentTable";
import Divider from "@material-ui/core/Divider";
const { Blockchain } = require("savjeecoin");

class BlockchainService extends React.Component {
  blockchainInstance = new Blockchain();

  constructor(props) {
    super(props);
    this.blockchainInstance.difficulty = this.props.setDiff;
    this.blockchainInstance.reward = this.props.setReward;
    this.blockchainInstance.minePendingTransactions("my-wallet-address");

    this.getBlocks = this.getBlocks.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  state = {
    selectedBlockId: null,
    difficulty: this.props.setDiff,
    reward: this.props.setReward,
  };

  handleClick = (blockId) => {
    console.log(blockId);
    this.setState({ selectedBlockId: blockId });
  };

  getBlocks() {
    return this.blockchainInstance.chain;
  }

  render() {
    return (
      <div>
        <BlockGrid
          onChange={this.handleClick}
          getBlocksHandler={this.getBlocks}
        />
        <Divider />
        <h1>Transaction inside Block temp</h1>
        <BlockContentTable
          blocks={this.getBlocks}
          blockId={this.state.selectedBlockId || 1}
          // status={user.id === this.state.selectedBlockId ? true : false}
        />
      </div>
    );
  }
}

export default BlockchainService;
